import React from 'react';
import { render } from 'react-dom';
import { Layout ,  Row, Col, Breadcrumb , Select,Form, Transfer,Button,Empty } from 'antd';
import 'antd/dist/antd.css';
import './index.css';

const { Header, Footer, Sider, Content } = Layout;
const { Option } = Select;
const layout = {
  labelCol: {
    span: 6,
  },
  wrapperCol: {
    span: 18,
  },
};

const tailLayout = {
  wrapperCol: { offset: 8, span: 16 },
};
function onBlur() {
  console.log('blur');
}

function onFocus() {
  console.log('focus');
}

function onSearch(val) {
  console.log('search:', val);
}


class App extends React.Component {
  state = {
    targetKeys: [],
    selectedKeys: [],
    mockData: [],
  };

  onChange = (value) => {
    if(value === "1"){
      console.log(`selected ${value}`);
      const mockData = []
      for (let i = 0; i < 20; i++) {
          mockData.push({
            key: i.toString(),
            title: `content${i + 1}`,
            description: `description of content${i + 1}`,
          });
      }
      this.setState({ mockData: mockData });
    }
  }


  handleChange = (nextTargetKeys, direction, moveKeys) => {
    this.setState({ targetKeys: nextTargetKeys });

    console.log('targetKeys: ', nextTargetKeys);
    console.log('direction: ', direction);
    console.log('moveKeys: ', moveKeys);
  };

  handleSelectChange = (sourceSelectedKeys, targetSelectedKeys) => {
    this.setState({ selectedKeys: [...sourceSelectedKeys, ...targetSelectedKeys] });

    console.log('sourceSelectedKeys: ', sourceSelectedKeys);
    console.log('targetSelectedKeys: ', targetSelectedKeys);
  };

  handleScroll = (direction, e) => {
    console.log('direction:', direction);
    console.log('target:', e.target);
  };



render() {
  const { targetKeys, selectedKeys, mockData } = this.state;
  // targetKeys = mockData.filter(item => +item.key % 3 > 1).map(item => item.key);

  return (
    <Layout>
    <Header>Header</Header>
    <Layout>
      <Sider>Sider</Sider>
      <Content>
      <Row style={{ padding: '0 24px 24px', backgroundColor: '#ffffff'}}>
      <Col span={24}><h1>ส่งออกข้อมูล</h1></Col>
      </Row>
      <Row style={{ margin: '24px 24px 24px 24px', backgroundColor: '#ffffff'}}>
          <Col span={24}>
          <Breadcrumb style={{ backgroundColor: '#ffffff'}}>
            <Breadcrumb.Item>หน้าหลัก</Breadcrumb.Item>
            <Breadcrumb.Item>ผู้ดูแลระบบ</Breadcrumb.Item>
            <Breadcrumb.Item>ส่งออกข้อมูล</Breadcrumb.Item>
          </Breadcrumb>
          </Col>
          <Col span={24}>
          <Form {...layout}  name="control-hooks" >
          <Form.Item {...layout}  name="employeeType" label="ประเภท">
            <Select
              showSearch
              style={{ width: 300 }}
              placeholder="การศึกษาพนักงาน"
              optionFilterProp="children"
              onChange={this.onChange}
              onFocus={onFocus}
              onBlur={onBlur}
              onSearch={onSearch}
              filterOption={(input, option) =>
                option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
              }
            >
            <Option value="1">E-Learning Upload Template (Report)</Option>
          </Select>
          </Form.Item>
          <Form.Item name="exportField" label="เลือกข้อมูลที่ต้องการ" >
          <div>
        <Transfer
          dataSource={mockData}
          titles={['ข้อมูลที่มีให้เลือก', 'ข้อมูลที่เลือก']}
          targetKeys={targetKeys}
          selectedKeys={selectedKeys}
          onChange={this.handleChange}
          onSelectChange={this.handleSelectChange}
          onScroll={this.handleScroll}
          render={item => item.title}
          locale={{
            itemUnit: 'ชิ้น',
            itemsUnit: 'ชิ้น',
            notFoundContent: <Empty image={Empty.PRESENTED_IMAGE_SIMPLE} description={<p>ไม่มีข้อมูล</p>}></Empty>,
          }}
        />
      </div>
      </Form.Item>
      <Form.Item
        wrapperCol={{
          xs: {
            span: 24,
            offset: 0,
          },
          sm: {
            span: 16,
            offset: 8,
          },
        }}
      >
        <Button type="primary" htmlType="submit">
          ส่งออก
        </Button>
        <Button type="info"  style={{marginLeft: "10px" }}>
          ยกเลิก
        </Button>
      </Form.Item>
          </Form>
          </Col>
      </Row>
      </Content>
    </Layout>
    <Footer>Footer</Footer>
    </Layout>
  );
 }
};

render(<App />, document.getElementById('root'));
